// Verifica si viene un session_id en el body
const body = $input.first().json.body || $input.first().json;
const sessionId = body.session_id || null;
const message = body.message;
const instructions = body.instructions || null;

if (!message) {
  throw new Error('El campo "message" es requerido');
}

return [{
  json: {
    session_id: sessionId,
    message: message,
    instructions: instructions,
    needs_new_session: !sessionId
  }
}];

// Determinar el session_id a usar
const preparados = $('Preparar Datos').first().json;
let sessionId;

// Si llegamos por la rama 'crear sesión'
try {
  const sesionCreada = $('Crear Sesión en Bridge').first();
  if (sesionCreada && sesionCreada.json.session_id) {
    sessionId = sesionCreada.json.session_id;
  }
} catch(e) {}

// Si no, usar el session_id del input original
if (!sessionId) {
  sessionId = preparados.session_id;
}

return [{
  json: {
    session_id: sessionId,
    message: preparados.message
  }
}];

const response = $input.first().json;
const sessionId = $('Unir Session ID').first().json.session_id;

return [{
  json: {
    success: true,
    session_id: sessionId,
    response_text: response.response_text,
    duration_ms: response.duration_ms,
    message: 'Guarda el session_id para continuar la conversación'
  }
}];
